    public void mostrarTiro(int tiro, int noJugador) {
        mensaje = mensaje + "jugador [" + noJugador + "] tir√≥: " + tiro + "\n";
        jTextArea1.setText(mensaje);
    }
    
    public void mostrarGanador(){
        mensaje += "gano jugador " + JugadorMejorTiro + " con " + mejorTiro() + "\n";
        jTextArea1.setText(mensaje);
    }
    
    private int mejorTiro(){
        int[] array = tiros;
        Arrays.sort(array);
        return array[2];
    }
    

    

